mapper.select:
SELECT prod_price, prod_stock, member_id, prod_id, prod_cate, prod_content, prod_name
FROM product
WHERE prod_id = #{prod_id}

mapper.update:
UPDATE product set(
	prod_price = #{prod_price}, 
	prod_stock = #{prod_stock}, 
	member_id = #{member_id}, 
	prod_cate = #{prod_cate}, 
	prod_content = #{prod_content}, 
	prod_name = #{prod_name}
)
WHERE prod_id = #{prod_id}
