mapper.select:
      SELECT review_id, member_id, postdate, title, content, star, evaluation, review_like, prod_id
      FROM (SELECT Tb.*, rownum rNum FROM (
				SELECT * FROM product
				ORDER BY prod_id DESC
			) Tb
			WHERE rownum<=#{end}
		) pro
		LEFT JOIN product_img img
			ON pro.prod_id = img.prod_id and img.main = 'main'
		WHERE rNum >=#{start}

mapper.update:
UPDATE review SET
title=#{title}, content=#{content}
WHERE review_id=#{review_id}
